class BSeller_SkyHub_Block_Adminhtml_Catalog_Product_Grid
    extends Mage_Adminhtml_Block_Catalog_Product_Grid
{
    /**
     * Add new action
     *
     * @return BSeller_SkyHub_Block_Adminhtml_Catalog_Product_Grid
     */
    protected function _prepareMassaction()
    {
        parent::_prepareMassaction();
        $this->getMassactionBlock()->addItem('add_product_queue_skyhub', array(
            'label'=> Mage::helper('catalog')->__('Add oriduct to Queue in Skyhub'),
            'url'  => $this->getUrl('*/bseller_skyhub_catalog_product_massQueue/addProductToQueue'),
            'confirm' => Mage::helper('catalog')->__('Are you sure?')
       ));
       return $this;
    }
}
